const ProductList: React.FC = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const [selectedPlatform, setSelectedPlatform] = useState<string | undefined>(undefined);
    const [filteredProducts, setFilteredProducts] = useState(products);
    const [savedProducts, setSavedProducts] = useState<number[]>([]);

    const platforms = ['Tất cả', 'Shopee', 'Lazada', 'Tiki']; // Add your platforms here
    const pageSize = 6; // Set your desired page size

    // Handle filtering based on search term and selected platform
    useEffect(() => {
        const filtered = products.filter((product) => {
            const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                                  product.url.toLowerCase().includes(searchTerm.toLowerCase());
            const matchesPlatform = selectedPlatform === 'All' || !selectedPlatform || product.category === selectedPlatform;
            return matchesSearch && matchesPlatform;
        });
        setFilteredProducts(filtered);
    }, [searchTerm, selectedPlatform]);

    // Handle saving products
    const handleSaveProduct = (productId: number) => {
        setSavedProducts([...savedProducts, productId]);
    };

    // Handle page change
    const handlePageChange = (page: number) => {
        setCurrentPage(page);
    };

    // Get the products for the current page
    const paginatedProducts = filteredProducts.slice(
        (currentPage - 1) * pageSize, currentPage * pageSize
    );

    return (
        <div className="py-10 min-h-screen">
            {/* Search and select */}
            <div className="flex justify-between mb-10">
                <div className='flex items-center'>
                    <span className='font-semibold mr-4'>Chọn sàn: </span>
                    <Select
                        defaultValue="Tất cả"
                        onChange={(value) => setSelectedPlatform(value)}
                        // className="py-4"
                        style={{ width: 200 }}
                    >
                    {platforms.map((platform, index) => (
                    <Option key={index} value={platform}>
                        {platform}
                    </Option>
                ))}
                </Select>
                </div>
                <Input.Search
                    placeholder="Tìm kiếm sản phẩm theo tên hoặc URL"
                    // prefix={<SearchOutlined />}
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="rounded-lg w-80"
                    // size="small"
                />
            </div>

            {/* Product list */}
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                {paginatedProducts.map((product) => (
                    <Card
                        key={product.id}
                        hoverable
                        className="relative rounded-lg overflow-hidden border border-blue-500"
                        cover={<img alt={product.name} src={product.image} className="h-60 w-full object-cover" />}
                        actions={[
                            <Button 
                                key={product.id}
                                icon={<SaveOutlined />} 
                                onClick={() => handleSaveProduct(product.id)}
                                disabled={savedProducts.includes(product.id)}
                                className=""
                            >
                                {savedProducts.includes(product.id) ? 'Đã lưu' : 'Lưu sản phẩm'}
                            </Button>,
                        ]}
                    >
                        <h2 className="font-bold text-lg">{product.name}</h2>
                        <p className="text-green-600 font-semibold">Giá: {product.price.toLocaleString()} VND</p>
                        <Button type="link" href={product.url} target="_blank" className="w-full mt-2">
                            Xem sản phẩm
                        </Button>
                        <div className='absolute right-0 top-0 p-2 text-blue-100 bg-blue-400'>
                            - {product.discount} %
                        </div>
                    </Card>
                ))}
            </div>

            {/* Pagination */}
            <Pagination
                current={currentPage}
                total={filteredProducts.length}
                pageSize={pageSize}
                onChange={handlePageChange}
                align='center'
                className="mt-8 text-center"
            />
        </div>
    );
};

export default ProductList;
